import React, { useState, useEffect } from "react";

export default function UnfundedCommitments() {
  const [lines, setLines] = useState([
    {
      label: "Commercial Loans",
      totalUnfundedCom: 0,
      unconditionallyCancellable: 0,
      netUnfunded: 0,
      estimatedProbabilityOfFunding: 0,
      wtdAverageRemaining: 0,
      annualExpected: 0,
      expectedLifetime: 0,
      impliedCecl: 0
    },
    {
      label: "Credit Card Lines",
      totalUnfundedCom: 0,
      unconditionallyCancellable: 0,
      netUnfunded: 0,
      estimatedProbabilityOfFunding: 0,
      wtdAverageRemaining: 0,
      annualExpected: 0,
      expectedLifetime: 0,
      impliedCecl: 0
    },
    {
      label: "Total Unfunded Commitments",
      totalUnfundedCom: 0,
      unconditionallyCancellable: 0,
      netUnfunded: 0,
      estimatedProbabilityOfFunding: 0,
      wtdAverageRemaining: 0,
      annualExpected: 0,
      expectedLifetime: 0,
      impliedCecl: 0
    }
  ]);

  useEffect(() => {
    let calculatedLines = lines.map(item => {
      return {
        label: item.label,
        totalUnfundedCom: item.totalUnfundedCom,
        unconditionallyCancellable: item.unconditionallyCancellable,
        netUnfunded: item.totalUnfundedCom - item.unconditionallyCancellable,
        estimatedProbabilityOfFunding: item.estimatedProbabilityOfFunding,
        wtdAverageRemaining: item.wtdAverageRemaining,
        annualExpected: item.annualExpected,
        expectedLifetime:
          item.estimatedProbabilityOfFunding +
          item.wtdAverageRemaining +
          item.annualExpected,
        impliedCecl: item.expectedLifetime * item.netUnfunded
      };
    });
  });

  const setTotalUnfundedCom = (e, index) => {
    let copy = lines.slice();
    let itemFromLines = copy[index];

    itemFromLines.totalUnfundedCom = e.target.value;
    setLines(copy);
  };

  const setUnconditionallyCancellable = (e, index) => {
    let copy = lines.slice();
    let itemFromLines = copy[index];

    itemFromLines.unconditionallyCancellable = e.target.value;
    setLines(copy);
  };

  const simpleLine = (item, index) => {
    return (
      <tr>
        <td>{item.label}</td>
        <td>
          <input
            onChange={e => {
              setTotalUnfundedCom(e, index);
            }}
            value={item.totalUnfundedCom}
          />
        </td>
        <td></td>
        <td>
          <input
            onChange={e => {
              setUnconditionallyCancellable(e, index);
            }}
            value={item.unconditionallyCancellable}
          />
        </td>
        <td></td>
        <td>{item.netUnfunded}</td>
        <td>
          <input value={item.estimatedProbabilityOfFunding} />
        </td>
        <td></td>
        <td>
          <input value={item.wtdAverageRemaining} />
        </td>
        <td></td>
        <td>
          <input value={item.annualExpected} />
        </td>
        <td></td>
        <td>123123</td>
        <td></td>
        <td>123123</td>
      </tr>
    );
  };

  return (
    <table>
      <thead>
        <tr>
          <td></td>
          <td colSpan="5">Unfunded Commitments:</td>
          <td colSpan="7">Expected Future Losses:</td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td></td>
          <td>(A)</td>
          <td>-</td>
          <td>(B)</td>
          <td>=</td>
          <td>(C)</td>
          <td>(D)</td>
          <td>x</td>
          <td>(E)</td>
          <td>x</td>
          <td>(F)</td>
          <td>=</td>
          <td>(G)</td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td></td>
          <td>Total</td>
          <td></td>
          <td>Unconditionally</td>
          <td></td>
          <td>Net</td>
          <td>Estimated</td>
          <td></td>
          <td>Wtd. Average</td>
          <td></td>
          <td>Annual</td>
          <td></td>
          <td>Expected</td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td></td>
          <td>Unfunded</td>
          <td></td>
          <td>Cancellable</td>
          <td></td>
          <td>Unfunded</td>
          <td>Probability of</td>
          <td></td>
          <td>Remaining</td>
          <td></td>
          <td>Expected</td>
          <td></td>
          <td>Lifetime</td>
          <td></td>
          <td>Implied</td>
        </tr>
        <tr>
          <td></td>
          <td>Commitment</td>
          <td></td>
          <td>Amount (1)</td>
          <td></td>
          <td>Commitment</td>
          <td>Funding (2)</td>
          <td></td>
          <td>Commit. (Yrs)</td>
          <td></td>
          <td>Loss Rate (3)</td>
          <td></td>
          <td>Loss Rate</td>
          <td></td>
          <td>CECL</td>
        </tr>
      </thead>
      <tbody>
        {lines.map((item, index) => {
          if (index === lines.length - 1) {
            return <tr></tr>;
          }
          return simpleLine(item, index);
        })}
      </tbody>
    </table>
  );
}
