import React, { useState } from "react";
import SummaryResults from "./SummaryResults";
import UnfundedCommitments from "./UnfundedCommitments";
import "./style.css";

function App() {
  const [ceclValue, setCeclValue] = useState(0);
  const [isSummary, setIsSummary] = useState(0);

  const formatAsPercentage = value => {
    return `${(value * 100).toFixed(2)}%`;
  };

  const formatAsDolarNumber = value => {
    const formatter = new Intl.NumberFormat("en-US", {
      style: "currency",
      currency: "USD",
      minimumFractionDigits: 2
    });

    return `${formatter.format(value)}`;
  };

  return (
    <div className="container">
      <div className="buttonsContainer">
        <button onClick={e => setIsSummary(true)}>Summary</button>
        <button onClick={e => setIsSummary(false)}>Undunfed</button>
      </div>

      {isSummary && (
        <SummaryResults
          formatAsPercentageCb={formatAsPercentage}
          formatAsDolarNumberCb={formatAsDolarNumber}
          ceclValue={ceclValue}
        />
      )}

      {!isSummary && (
        <UnfundedCommitments
          ceclValue={ceclValue}
          setCeclValueCb={setCeclValue}
        />
      )}
    </div>
  );
}

export default App;
