import React, { useEffect, useState } from "react";
import axios from "axios";

export default function SummaryResults({
  formatAsPercentageCb,
  formatAsDolarNumberCb
}) {
  const [lines, setLines] = useState([
    {
      fieldId: 1,
      label: "Unsecured Credit Card Loans",
      years: 5,
      averageLoans: 0,
      netChargeoffsSum: 0,
      historicalRate: 0,
      userAdjustments: 0,
      futureRate: 0,
      currentBalance: 0,
      impliedResult: 0
    },
    {
      fieldId: 2,
      label: "New Vehicle Loans",
      years: 4,
      averageLoans: 0,
      netChargeoffsSum: 0,
      historicalRate: 0,
      userAdjustments: 0,
      futureRate: 0,
      currentBalance: 0,
      impliedResult: 0
    }
  ]);

  const loadLineByYearsAndFieldId = (years, fieldId) => {
    axios
      .get(
        `https://react-code-challenge.herokuapp.com/codeChallenge?fieldId=${fieldId}&years=${years}`
      )
      .then(result => {
        const { averageLoans, netChargeoffsSum } = result.data;

        let linesCopy = lines.slice();
        let item = linesCopy.filter(item => item.fieldId === fieldId)[0];
        item.averageLoans = averageLoans;
        item.netChargeoffsSum = netChargeoffsSum;
        item.historicalRate = netChargeoffsSum / averageLoans;

        item.futureRate =
          parseFloat(item.historicalRate) +
          parseFloat(item.userAdjustments / 100);

        item.impliedResult = item.futureRate * item.currentBalance;

        setLines(linesCopy);
      });
  };

  useEffect(() => {
    loadLineByYearsAndFieldId(lines[0].years, lines[0].fieldId);
    loadLineByYearsAndFieldId(lines[1].years, lines[1].fieldId);
  }, []);

  const setYears = (e, item) => {
    if (e.target.value > 5 || e.target.value < 0) {
      return;
    }

    let copy = lines.slice();
    let itemFromLines = copy.filter(t => t.fieldId === item.fieldId).slice();

    itemFromLines[0].years = e.target.value;
    setLines(copy);
  };

  const setUserAdjustments = (e, item) => {
    let copy = lines.slice();
    let itemFromLines = copy.filter(t => t.fieldId === item.fieldId).slice();

    itemFromLines[0].userAdjustments = e.target.value;
    setLines(copy);
  };

  const setCurrentBalance = (e, item) => {
    let copy = lines.slice();
    let itemFromLines = copy.filter(t => t.fieldId === item.fieldId).slice();

    itemFromLines[0].currentBalance = e.target.value;
    setLines(copy);
  };

  return (
    <div>
      <table>
        <thead>
          <tr>
            <td colSpan="4"></td>
            <td colSpan="5">Life of Loan Net Chargeoff Rate:</td>
            <td></td>
            <td colSpan="3">Current Expected Credit Loss</td>
          </tr>
          <tr>
            <td colSpan="4"></td>
            <td>(A)</td>
            <td>+</td>
            <td>(B)</td>
            <td>=</td>
            <td>(C)</td>
            <td></td>
            <td>(D)</td>
            <td></td>
            <td>(D x C)</td>
          </tr>
          <tr>
            <td></td>
            <td>WARM</td>
            <td>Avg</td>
            <td>Agg</td>
            <td>Historical</td>
            <td></td>
            <td>User</td>
            <td></td>
            <td>Future</td>
            <td></td>
            <td>Current</td>
            <td></td>
            <td>Implied</td>
          </tr>
          <tr>
            <td></td>
            <td>In Years</td>
            <td>Balance</td>
            <td>Net Chargeoffs</td>
            <td>Rate</td>
            <td></td>
            <td>Adjustment </td>
            <td></td>
            <td>Rate</td>
            <td></td>
            <td>Balance</td>
            <td></td>
            <td>Result</td>
          </tr>
        </thead>
        <tbody>
          {lines.map(item => {
            return (
              <tr>
                <td>{item.label}</td>
                <td>
                  <input
                    onBlur={e =>
                      loadLineByYearsAndFieldId(item.years, item.fieldId)
                    }
                    onChange={e => setYears(e, item)}
                    value={item.years}
                  />
                </td>
                <td>{item.averageLoans} </td>
                <td>{item.netChargeoffsSum}</td>
                <td>{formatAsPercentageCb(item.historicalRate)}</td>
                <td></td>
                <td>
                  <input
                    onBlur={e =>
                      loadLineByYearsAndFieldId(item.years, item.fieldId)
                    }
                    onChange={e => setUserAdjustments(e, item)}
                    value={item.userAdjustments}
                  />
                </td>
                <td></td>
                <td> {formatAsPercentageCb(item.futureRate)}</td>
                <td></td>
                <td>
                  <input
                    onBlur={e => {
                      loadLineByYearsAndFieldId(item.years, item.fieldId);
                    }}
                    onChange={e => setCurrentBalance(e, item)}
                    value={item.currentBalance}
                  />
                </td>
                <td></td>
                <td>{formatAsDolarNumberCb(item.impliedResult)}</td>
              </tr>
            );
          })}
          <tr>
            <td colSpan="4"></td>
            <td style={{ background: "lightblue" }} colSpan="5"></td>
            <td style={{ background: "lightblue" }}>20%</td>
            <td style={{ background: "lightblue" }} colSpan="2">
              Bank ALL
            </td>
            <td>
              <input value="9874894" />
            </td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}
