import React, { useState, useEffect } from "react";

export default function UnfundedCommitments() {
  const [lines, setLines] = useState([
    {
      label: "Commercial Loans",
      totalUnfundedCom: 0,
      unconditionallyCancellable: 0,
      netUnfunded: 0,
      estimatedProbabilityOfFunding: 0,
      wtdAverageRemaining: 0,
      annualExpected: 0,
      expectedLifetime: 0,
      impliedCecl: 0
    },
    {
      label: "Credit Card Lines",
      totalUnfundedCom: 0,
      unconditionallyCancellable: 0,
      netUnfunded: 0,
      estimatedProbabilityOfFunding: 0,
      wtdAverageRemaining: 0,
      annualExpected: 0,
      expectedLifetime: 0,
      impliedCecl: 0
    },
    {
      label: "Total Unfunded Commitments",
      totalUnfundedCom: 0,
      unconditionallyCancellable: 0,
      netUnfunded: 0,
      estimatedProbabilityOfFunding: 0,
      wtdAverageRemaining: 0,
      annualExpected: 0,
      expectedLifetime: 0,
      impliedCecl: 0
    }
  ]);

  useEffect(() => {
    calculateAllValues();
  }, []);

  const calculateAllValues = () => {
    let calculatedLines = lines.map(item => {
      const label = item.label;
      const totalUnfundedCom = item.totalUnfundedCom;
      const unconditionallyCancellable = item.unconditionallyCancellable;
      const netUnfunded = totalUnfundedCom - unconditionallyCancellable;
      const estimatedProbabilityOfFunding = item.estimatedProbabilityOfFunding;
      const wtdAverageRemaining = item.wtdAverageRemaining;
      const annualExpected = item.annualExpected;
      const expectedLifetime =
        parseFloat(estimatedProbabilityOfFunding) *
        parseInt(wtdAverageRemaining) *
        parseFloat(annualExpected);
      const impliedCecl = expectedLifetime * netUnfunded;

      calculateTotalValues();
      return {
        label,
        totalUnfundedCom,
        unconditionallyCancellable,
        netUnfunded,
        estimatedProbabilityOfFunding,
        wtdAverageRemaining,
        annualExpected,
        expectedLifetime,
        impliedCecl
      };
    });

    setLines(calculatedLines);
  };

  const calculateTotalValues = () => {
    console.log("TOTAL");
    const total = lines.reduce((total, currentItem) => {
      const sum = {
        totalUnfundedCom:
          parseInt(total.totalUnfundedCom) +
          parseInt(currentItem.totalUnfundedCom),
        totalUnconditionallyCancellable:
          parseInt(total.unconditionallyCancellable) +
          parseInt(currentItem.unconditionallyCancellable),
        totalNetUnfunded:
          parseInt(total.netUnfunded) + parseInt(currentItem.netUnfunded),
        totalImpliedCecl:
          parseInt(total.impliedCecl) + parseInt(currentItem.impliedCec)
      };
      return sum;
    });
    let linesCopy = lines.slice();
    let item = linesCopy.filter((item, index) => index === lines.length - 1)[0];

    item.totalUnfundedCom = total.totalUnfundedCom;

    console.log(linesCopy);

    setLines(linesCopy);
  };

  const setTotalUnfundedCom = (e, index) => {
    let copy = lines.slice();
    let itemFromLines = copy[index];

    itemFromLines.totalUnfundedCom = e.target.value;
    setLines(copy);
  };

  const setUnconditionallyCancellable = (e, index) => {
    let copy = lines.slice();
    let itemFromLines = copy[index];

    itemFromLines.unconditionallyCancellable = e.target.value;
    setLines(copy);
  };

  const setEstimatedProbabilityOfFunding = (e, index) => {
    let copy = lines.slice();
    let itemFromLines = copy[index];

    itemFromLines.estimatedProbabilityOfFunding = e.target.value;
    setLines(copy);
  };

  const setWtdAverageRemaining = (e, index) => {
    let copy = lines.slice();
    let itemFromLines = copy[index];

    itemFromLines.wtdAverageRemaining = e.target.value;
    setLines(copy);
  };

  const setAnnualExpected = (e, index) => {
    let copy = lines.slice();
    let itemFromLines = copy[index];

    itemFromLines.annualExpected = e.target.value;
    setLines(copy);
  };

  const simpleLine = (item, index) => {
    return (
      <tr>
        <td>{item.label}</td>
        <td>
          <input
            onBlur={calculateAllValues}
            onChange={e => {
              setTotalUnfundedCom(e, index);
            }}
            value={item.totalUnfundedCom}
          />
        </td>
        <td></td>
        <td>
          <input
            onBlur={calculateAllValues}
            onChange={e => {
              setUnconditionallyCancellable(e, index);
            }}
            value={item.unconditionallyCancellable}
          />
        </td>
        <td></td>
        <td>{item.netUnfunded}</td>
        <td>
          <input
            onBlur={calculateAllValues}
            onChange={e => {
              setEstimatedProbabilityOfFunding(e, index);
            }}
            value={item.estimatedProbabilityOfFunding}
          />
        </td>
        <td></td>
        <td>
          <input
            onBlur={calculateAllValues}
            onChange={e => {
              setWtdAverageRemaining(e, index);
            }}
            value={item.wtdAverageRemaining}
          />
        </td>
        <td></td>
        <td>
          <input
            onBlur={calculateAllValues}
            onChange={e => {
              setAnnualExpected(e, index);
            }}
            value={item.annualExpected}
          />
        </td>
        <td></td>
        <td>{item.expectedLifetime}</td>
        <td></td>
        <td>{item.impliedCecl}</td>
      </tr>
    );
  };

  const totalLine = (item, index) => {
    return (
      <tr>
        <td>{item.label}</td>
        <td>{item.totalUnfundedCom}</td>
        <td></td>
        <td>{item.unconditionallyCancellable}</td>
        <td></td>
        <td>{item.netUnfunded}</td>
        <td colSpan="6"></td>
        <td>{item.expectedLifetime}</td>
        <td></td>
        <td>{item.impliedCecl}</td>
      </tr>
    );
  };

  return (
    <table>
      <thead>
        <tr>
          <td></td>
          <td colSpan="5">Unfunded Commitments:</td>
          <td colSpan="7">Expected Future Losses:</td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td></td>
          <td>(A)</td>
          <td>-</td>
          <td>(B)</td>
          <td>=</td>
          <td>(C)</td>
          <td>(D)</td>
          <td>x</td>
          <td>(E)</td>
          <td>x</td>
          <td>(F)</td>
          <td>=</td>
          <td>(G)</td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td></td>
          <td>Total</td>
          <td></td>
          <td>Unconditionally</td>
          <td></td>
          <td>Net</td>
          <td>Estimated</td>
          <td></td>
          <td>Wtd. Average</td>
          <td></td>
          <td>Annual</td>
          <td></td>
          <td>Expected</td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td></td>
          <td>Unfunded</td>
          <td></td>
          <td>Cancellable</td>
          <td></td>
          <td>Unfunded</td>
          <td>Probability of</td>
          <td></td>
          <td>Remaining</td>
          <td></td>
          <td>Expected</td>
          <td></td>
          <td>Lifetime</td>
          <td></td>
          <td>Implied</td>
        </tr>
        <tr>
          <td></td>
          <td>Commitment</td>
          <td></td>
          <td>Amount (1)</td>
          <td></td>
          <td>Commitment</td>
          <td>Funding (2)</td>
          <td></td>
          <td>Commit. (Yrs)</td>
          <td></td>
          <td>Loss Rate (3)</td>
          <td></td>
          <td>Loss Rate</td>
          <td></td>
          <td>CECL</td>
        </tr>
      </thead>
      <tbody>
        {lines.map((item, index) => {
          if (index === lines.length - 1) {
            return totalLine(item, index);
          }
          return simpleLine(item, index);
        })}
      </tbody>
    </table>
  );
}
