import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [lines, setLines] = useState([
    {
      fieldId: 1,
      label: "Test",
      years: 5,
      averageLoans: 0,
      netChargeoffsSum: 0,
      historicalRate: 0
    },
    {
      fieldId: 2,
      label: "Test2",
      years: 4,
      averageLoans: 0,
      netChargeoffsSum: 0,
      historicalRate: 0
    }
  ]);

  const loadLineByYearsAndFieldId = (years, fieldId) => {
    axios
      .get(
        `https://react-code-challenge.herokuapp.com/codeChallenge?fieldId=${fieldId}&years=${years}`
      )
      .then(result => {
        const { averageLoans, netChargeoffsSum } = result.data;

        let linesCopy = lines.slice();
        let item = linesCopy.filter(item => item.fieldId === fieldId);
        item[0].averageLoans = averageLoans;
        item[0].netChargeoffsSum = netChargeoffsSum;
        item[0].historicalRate = netChargeoffsSum / averageLoans;

        setLines(linesCopy);
      });
  };

  useEffect(() => {
    loadLineByYearsAndFieldId(lines[0].years, lines[0].fieldId);
  }, []);

  useEffect(() => {
    loadLineByYearsAndFieldId(lines[1].years, lines[1].fieldId);
  }, []);

  const setYears = (e, item) => {
    if (e.target.value > 5 || e.target.value < 0) {
      return;
    }
    console.log(e.target.value);

    let copy = lines.slice();
    let itemFromLines = copy.filter(t => t.fieldId === item.fieldId).slice();

    itemFromLines[0].years = e.target.value;
    setLines(copy);
  };

  const formatAsPercentage = value => {
    return `${(value * 100).toFixed(2)}%`;
  };

  return (
    <div>
      <table>
        <thead>
          <tr>
            <td colSpan="4"></td>
            <td colSpan="5">Life of Loan Net Chargeoff Rate:</td>
            <td></td>
            <td colSpan="3">Current Expected Credit Loss</td>
          </tr>
          <tr>
            <td colSpan="4"></td>
            <td>(A)</td>
            <td>+</td>
            <td>(B)</td>
            <td>=</td>
            <td>(C)</td>
            <td></td>
            <td>(D)</td>
            <td></td>
            <td>(D x C)</td>
          </tr>
          <tr>
            <td></td>
            <td>WARM</td>
            <td>Avg</td>
            <td>Agg</td>
            <td>Historical</td>
            <td></td>
            <td>User</td>
            <td></td>
            <td>Future</td>
            <td></td>
            <td>Current</td>
            <td></td>
            <td>Implied</td>
          </tr>
          <tr>
            <td></td>
            <td>In Years</td>
            <td>Balance</td>
            <td>Net Chargeoffs</td>
            <td>Rate</td>
            <td></td>
            <td>Adjustment </td>
            <td></td>
            <td>Rate</td>
            <td></td>
            <td>Balance</td>
            <td></td>
            <td>Result</td>
          </tr>
        </thead>
        <tbody>
          {lines.map(item => {
            return (
              <tr>
                <td>{item.label}</td>
                <td>
                  <input
                    onBlur={e =>
                      loadLineByYearsAndFieldId(item.years, item.fieldId)
                    }
                    onChange={e => setYears(e, item)}
                    value={item.years}
                  />
                </td>
                <td>{item.averageLoans} </td>
                <td>{item.netChargeoffsSum}</td>
                <td>{formatAsPercentage(item.historicalRate)}</td>
                <td></td>
                <td>
                  <input value="5" />
                </td>
                <td></td>
                <td>$78,690 </td>
                <td></td>
                <td>
                  <input value="5" />
                </td>
                <td></td>
                <td>$78,690 </td>
              </tr>
            );
          })}
          {/* <tr>
            <td>Unsecured Credit Card Loans</td>
            <td>
              <input value="5" />
            </td>
            <td>$1,639,030 </td>
            <td>$78,690 </td>
            <td>
              <input value="5" />{" "}
            </td>
            <td>$78,690 </td>
            <td>$78,690 </td>
            <td>
              <input value="5" />{" "}
            </td>
            <td>$78,690 </td>
          </tr>
          <tr>
            <td>New Vehicle Loans</td>
            <td>
              <input value="4" />
            </td>
            <td></td>
            <td></td>
          </tr>
          <tr>
            <td>Total Gross Loans</td>
            <td></td>
            <td></td>
            <td></td>
          </tr> */}
        </tbody>
      </table>
    </div>
  );
}

export default App;
