import React, { useEffect, useState } from "react";
import axios from "axios";

const lines = {
  1: {
    label: "Test",
    years: 5,
    avgBalance: 123,
    netChargeOffs: 321
  },
  2: {
    label: "Test2",
    years: 4,
    avgBalance: 321,
    netChargeOffs: 321
  }
};

function App() {
  const [lines, setLines] = useState([
    {
      label: "Test",
      years: 5,
      averageLoans: 123,
      netChargeoffsSum: 321
    },
    {
      label: "Test2",
      years: 4,
      averageLoans: 321,
      netChargeoffsSum: 321
    }
  ]);

  useEffect(() => {
    axios
      .get(
        `https://react-code-challenge.herokuapp.com/codeChallenge?fieldId=1&years=${lines[0].years}`
      )
      .then(result => {
        let test = [...lines];
        console.log(test);

        let item = test[0];
        item.averageLoans = result.data.averageLoans;
        item.netChargeoffsSum = result.data.netChargeoffsSum;

        console.log(test);
        // setLines(lines);
      });
  }, [lines[0].years]);

  useEffect(() => {
    axios
      .get(
        `https://react-code-challenge.herokuapp.com/codeChallenge?fieldId=2&years=${lines[1].years}`
      )
      .then(result => {
        let item = lines[1];
        item.averageLoans = result.averageLoans;
        item.netChargeoffsSum = result.netChargeoffsSum;
      });
  }, [lines[1].years]);

  return (
    <div>
      <table>
        <thead>
          <tr>
            <td colSpan="4"></td>
            <td colSpan="5">Life of Loan Net Chargeoff Rate:</td>
            <td></td>
            <td colSpan="3">Current Expected Credit Loss</td>
          </tr>
          <tr>
            <td colSpan="4"></td>
            <td>(A)</td>
            <td>+</td>
            <td>(B)</td>
            <td>=</td>
            <td>(C)</td>
            <td></td>
            <td>(D)</td>
            <td></td>
            <td>(D x C)</td>
          </tr>
          <tr>
            <td></td>
            <td>WARM</td>
            <td>Avg</td>
            <td>Agg</td>
            <td>Historical</td>
            <td></td>
            <td>User</td>
            <td></td>
            <td>Future</td>
            <td></td>
            <td>Current</td>
            <td></td>
            <td>Implied</td>
          </tr>
          <tr>
            <td></td>
            <td>In Years</td>
            <td>Balance</td>
            <td>Net Chargeoffs</td>
            <td>Rate</td>
            <td></td>
            <td>Adjustment </td>
            <td></td>
            <td>Rate</td>
            <td></td>
            <td>Balance</td>
            <td></td>
            <td>Result</td>
          </tr>
        </thead>
        <tbody>
          {lines.map(item => {
            return (
              <tr>
                <td>{item.label}</td>
                <td>
                  <input value="5" />
                </td>
                <td>{item.averageLoans} </td>
                <td>{item.netChargeoffsSum}</td>
                <td>
                  <input value="5" />{" "}
                </td>
                <td>$78,690 </td>
                <td>$78,690 </td>
                <td>
                  <input value="5" />{" "}
                </td>
                <td>$78,690 </td>
              </tr>
            );
          })}
          <tr>
            <td>Unsecured Credit Card Loans</td>
            <td>
              <input value="5" />
            </td>
            <td>$1,639,030 </td>
            <td>$78,690 </td>
            <td>
              <input value="5" />{" "}
            </td>
            <td>$78,690 </td>
            <td>$78,690 </td>
            <td>
              <input value="5" />{" "}
            </td>
            <td>$78,690 </td>
          </tr>
          <tr>
            <td>New Vehicle Loans</td>
            <td>
              <input value="4" />
            </td>
            <td></td>
            <td></td>
          </tr>
          <tr>
            <td>Total Gross Loans</td>
            <td></td>
            <td></td>
            <td></td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default App;
